INSERT INTO `course_section` (`id`, `course_id`, `section_id`, `content`, `status`, `created_at`, `updated_at`, `deleted_at`) VALUES
	(4, 1, 10, '<p><span style="font-size: 11pt; font-family: verdana,geneva; color: #999999;">Ap&oacute;s oito edi&ccedil;&otilde;es do Simp&oacute;sio Nacional de Cartografia Geot&eacute;cnica, que contemplaram in&uacute;meras e relevantes discuss&otilde;es acerca dos m&eacute;todos e defini&ccedil;&atilde;o de par&acirc;metros para sua elabora&ccedil;&atilde;o, das escalas de abordagem, e sobretudo da aplica&ccedil;&atilde;o das cartas geot&eacute;cnicas, esse instrumento est&aacute;, hoje, incorporado ao planejamento territorial-urbano. Com a Lei 12.608/12, que institui a Pol&iacute;tica Nacional de Prote&ccedil;&atilde;o e Defesa Civil &ndash; PNPDEC, um de seus principais objetivos consiste na redu&ccedil;&atilde;o do risco de desastre e nas a&ccedil;&otilde;es de prote&ccedil;&atilde;o e defesa civil por meio da gest&atilde;o territorial e do planejamento das pol&iacute;ticas setoriais. Seu artigo 22 prev&ecirc; a altera&ccedil;&atilde;o da Lei no 12.340, de 1&ordm;de dezembro de 2010, que passa a vigorar considerando que os munic&iacute;pios dever&atilde;o elaborar <strong>Carta Geot&eacute;cnica de Aptid&atilde;o &agrave; Urbaniza&ccedil;&atilde;o</strong>, estabelecendo diretrizes urban&iacute;sticas voltadas para a seguran&ccedil;a dos novos parcelamentos do solo. Assim, o 9&ordm; SBCGG ser&aacute; uma oportunidade&iacute;mpar para discutir os resultados atuais e os rumos da Cartografia Geot&eacute;cnica no pa&iacute;s no &acirc;mbito t&eacute;cnico-cient&iacute;fico e das pol&iacute;ticas p&uacute;blicas.</span></p>\r\n<p>&nbsp;</p>\r\n<p><span style="font-size: 11pt; font-family: verdana,geneva; color: #999999;"><img src="http://abge1.hospedagemdesites.ws/uploads/arquivos/archivoseccion_202_logosbcgg.png" alt="" width="400" height="197" /></span></p>', '', '0000-00-00 00:00:00', '2014-10-30 13:46:39', NULL),
	(2, 1, 3, '', '', '0000-00-00 00:00:00', '2014-10-02 18:42:04', NULL),
	(3, 1, 4, '', '', '0000-00-00 00:00:00', '2014-10-02 18:42:30', NULL),
